    <script>
      function openinChatbot(){
         document.getElementById("createopeninchatbot").disabled='disabled';

         svgString = new XMLSerializer().serializeToString(document.querySelector('svg'));
         canvas = document.getElementById("canvas");
         ctx = canvas.getContext("2d");
         ctx.clearRect(0, 0, canvas.width, canvas.height);

         DOMURL = self.URL || self.webkitURL || self;
         img = new Image();
         svg = new Blob([svgString], {type: "image/svg+xml;charset=utf-8"});
         url = DOMURL.createObjectURL(svg);

        img.onload = function() {
          ctx.drawImage(img, 0, 0);
          png = canvas.toDataURL("image/png");
          document.querySelector('#png-container').innerHTML = "";
          document.querySelector('#png-container').innerHTML = '<img src="'+png+'"/>';
          document.getElementById("imgavatar").value = png;
          document.getElementById('openchatbot').submit();
        };
        img.src = url;
      }
    </script>

    <script>
      function generaPNG(){
         svgString = new XMLSerializer().serializeToString(document.querySelector('svg'));
         canvas = document.getElementById("canvas");
         ctx = canvas.getContext("2d");
         ctx.clearRect(0, 0, canvas.width, canvas.height);

         DOMURL = self.URL || self.webkitURL || self;
         img = new Image();
         svg = new Blob([svgString], {type: "image/svg+xml;charset=utf-8"});
         url = DOMURL.createObjectURL(svg);

        img.onload = function() {
          ctx.drawImage(img, 0, 0);

          png = canvas.toDataURL("image/png");
          document.querySelector('#png-container').innerHTML = "";
          document.querySelector('#png-container').innerHTML = '<img src="'+png+'"/>';

          var element = document.createElement('a');
          element.setAttribute('href', png);
          element.setAttribute('download',  "Avatar.png");
          element.style.display = 'none';
          document.body.appendChild(element);
          element.click();

          document.body.removeChild(element);
          DOMURL.revokeObjectURL(png);

        };
        img.src = url;
      }
    </script>

    <script>
      function generaSVG(){
        svgString = new XMLSerializer().serializeToString(document.querySelector('svg'));

        var element = document.createElement('a');
        element.setAttribute('href', "data:image/svg+xml;charset=utf-8," + encodeURIComponent(svgString));
        element.setAttribute('download',  "Avatar.svg");
        element.style.display = 'none';
        document.body.appendChild(element);
        element.click();
        document.body.removeChild(element);
      }
    </script>



