    <script>
      function randomAvatar(){
        var x, i;
        x = document.querySelectorAll("g");
        var body = Math.floor(Math.random() * 30);
        var head = Math.floor(Math.random() * 50);
        var face = Math.floor(Math.random() * 33);
        var accessories = Math.floor(Math.random() * 10);
        var facialhair = Math.floor(Math.random() * 16);

        for (i = 0; i < x.length; i++) {
          if(x[i].getAttribute('id').substr(0,4) == "body"){
            if(body == 1){
              document.getElementById("BODY").innerHTML = document.getElementById(x[i].getAttribute('id')).innerHTML;
            }
            body--;
          }

          if(x[i].getAttribute('id').substr(0,4) == "head"){
            if(head == 1){
              document.getElementById("HEAD").innerHTML = document.getElementById(x[i].getAttribute('id')).innerHTML;
            }
            head--;
          }

          if(x[i].getAttribute('id').substr(0,4) == "face"){
            if(face == 1){
                document.getElementById("FACE").innerHTML = document.getElementById(x[i].getAttribute('id')).innerHTML;
            }
            face--;
          }

          if(x[i].getAttribute('id').substr(0,11) == "facial-hair"){
            if(facialhair == 1){
              if((x[i].getAttribute('id') == "facial-hair/none") || (Math.floor(Math.random() * 2) == 1 )){
                document.getElementById("FACIAL-HAIR").innerHTML = "";
              }
              else{
                document.getElementById("FACIAL-HAIR").innerHTML = document.getElementById(x[i].getAttribute('id')).innerHTML;
              }
            }
            facialhair--;
          }

          if(x[i].getAttribute('id').substr(0,11) == "accessories"){
            if(accessories == 0){
              if((x[i].getAttribute('id') == "accessories/-None") || (Math.floor(Math.random() * 2) == 1 )){
                document.getElementById("ACCESORIES").innerHTML = "";
              }
              else{
                document.getElementById("ACCESORIES").innerHTML = document.getElementById(x[i].getAttribute('id')).innerHTML;
              }
            }
            accessories--;
          }
        }

        colore = "#" + Math.floor(Math.random() * 181).toString(16) + Math.floor(Math.random() * 181).toString(16) + Math.floor(Math.random() * 181).toString(16);

        document.getElementById("clothcolor").value = colore;
        updateAllCLOTHH(colore);

        colore = "#" + Math.floor(Math.random() * (181-20) + 20).toString(16) + Math.floor(Math.random() * (181-20) + 20).toString(16) + Math.floor(Math.random() * (181-20) + 20).toString(16);

        if(Math.floor(Math.random() * 2)){
          colore = "#D08B5B";
        }

        document.getElementById("skincolor").value = colore;
        updateAllSKIN(colore);

        colore = "#" + Math.floor(Math.random() * 100).toString(16) + Math.floor(Math.random() * 100).toString(16) + Math.floor(Math.random() * 100).toString(16);

        if(Math.floor(Math.random() * 2)){
          colore = "#000000";
        }

        document.getElementById("haircolor").value = colore;
        updateAllHAIRR(colore);
      }

    </script>




